import React, {useState, useEffect} from 'react';
import Header from "./Header";
import youtube from '../apis/youtube';
import {Grid, 
        Card,
        CardHeader,
        CardContent,
        CardActions,
        CardMedia,
        makeStyles,
        Typography
    } from '@material-ui/core';

const useStyles = makeStyles((theme) =>({
    toolbar: theme.mixins.toolbar,
    card:{
        minWidth: 275,
    },
    media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
 }));
export default function OtherVideos(){
    const [data,setData] = useState();
    //every time this view loads we get videos
    useEffect(()=>{
        async function getOtherVids(){
            let response = await youtube.get('search', {params:{
            q:'-COVID -19 -Vaccine -Podcast',
           }});
           setData(response.data);
        }
        getOtherVids();
    },[]);
    const classes  = useStyles();
    return(

         <div className="container">
            <Header />
            {/*Create space between grid & app bar*/}
            <div className={classes.toolbar}></div>
                <Grid container 
                  direction="column"
                  spacing={3}
                  justifyContent='center'
                  alignItems='center'
            >
            {data.length > 0 ?(
                    data.map((item) => (
                        <Grid item xs={12} sm={6}>
                            <Card>
                                  <CardMedia 
                                             component="iframe"
                                             title="test"
                                             src={`https://www.youtube.com/embed/${item.id.videoId}`}  
                                    />
                                    <CardContent>
                                        <Typography align="center" gutterBottom variant="h5">{item.snippet.title}</Typography>
                                        <Typography align="left" variant="subtitle">Description:{item.snippet.description}</Typography> 
                                        <Typography align="left" paragraph>Date:{item.snippet.publishedAt}</Typography>
                                    </CardContent>
                            </Card>
                        </Grid>
                ))   
                ):(
                    <Grid item xs={12}>
                        <Typography variant="h5">No Such Videos Exist in this Category. Please try the Other List.</Typography>
                    </Grid>

                )}
            </Grid>
        </div>
    );
}
