import React, {useState, useEffect} from 'react';
import Header from "./Header";
import {Grid, 
        Card,
        CardContent,
        CardMedia,
        makeStyles,
        Typography
    } from '@material-ui/core';
import youtube from '../apis/youtube';
const playlistId = 'PLogA9DP2_vSekxHP73PXaKD6nbOK56CJw';
 const useStyles = makeStyles((theme) =>({
    toolbar: theme.mixins.toolbar,
    gridContainer:{
        minHeight:"100vh",
    },
    media:{
        width:"80%",
    }
 }));
export default function CovidVideos(){
    const [data,setData] = useState([]);
    useEffect(()=>{
       async function getCovidVids(){
           let response = await youtube.get('/search', {params:{
            q:'COVID-19 Vaccine Podcast',
           }}).catch(function(error){
               console.log("ERROR!!", error.response);
           });
           setData(response.data.items);
       }
       getCovidVids();

    },[]);
    const classes  = useStyles();

    return(
        <div className="container">
            <Header />
            {/*Create space between grid & app bar*/}
            <div className={classes.toolbar}> </div>
                <Grid container 
                      direction="column"
                      spacing={3}
                      justifyContent='center'
                      alignItems='center'
                      className={classes.gridContainer}
                >
                {/**Take Json input from search term and display items in Card. */}
                {data.length > 0 ?(
                    data.map((item) => (
                        <Grid item xs={12} sm={6}>
                            <Card>
                                  <CardMedia 
                                             component="iframe"
                                             title="test"
                                             src={`https://www.youtube.com/embed/${item.id.videoId}`}  
                                    />
                                    <CardContent>
                                        <Typography align="center" gutterBottom variant="h5">{item.snippet.title}</Typography>
                                        <Typography align="left" variant="subtitle">Description:{item.snippet.description}</Typography> 
                                        <Typography align="left" paragraph>Date:{item.snippet.publishedAt}</Typography>
                                    </CardContent>
                            </Card>
                        </Grid>
                ))   
                ):(
                    <Grid item xs={12}>
                        <Typography variant="h5">No Such Videos Exist in this Category. Please try the Other List.</Typography>
                    </Grid>

                )}
                </Grid>
        </div>
    );
}
